import React, { Component } from 'react'
import Board from './components/Board'
import './App.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
export default class App extends Component {
  constructor(props){
    super(props)
    this.state ={
      username:"dien",
      nextPlayer: true,
      squareList:['','','','','','','','',''],
      isWin:false,
      playerWin:'',
      history:[]
    }
  }
  setParentsState=(obj)=>{
    this.setState(obj)
  }
  removeItem(index){
    let a=this.state.history;
    a=a.slice(0,index);
    this.state.history=a;
  }
  render() {

    return (
      <div>
        <h1>Tic Tac Toe</h1>
        <h3>User name : {this.state.username}</h3>
        <h4> nextPlayer:{this.state.nextPlayer}</h4>
        <Board squareList={this.state.squareList} 
        setParentsState={this.setParentsState}
        nextPlayer={this.state.nextPlayer}
        isWin={this.state.isWin}
        playerWin={this.state.playerWin}
        history={this.state.history}  
          />
        <h1>
          {this.state.playerWin}
        </h1>
        <ol>
    {this.state.history.map(item=><div className="removeButton"><Button  variant="light" onclick={()=>removeItem(item[1])}>remove</Button>{item[0]} -{item[1]}</div>)}
        </ol>
        <ol>
          Ranking
        </ol>
      </div>
    )
  }
}
