import React, { Component } from 'react'
import Board from './components/Board'
import './App.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
export default class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      username: "dien",
      nextPlayer: true,
      squareList: ['', '', '', '', '', '', '', '', ''],
      isWin: false,
      playerWin: '',
      history: [],
      count: 0
    }
  }
  setParentsState = (obj) => {
    this.setState(obj)
  }
  showRemoveItem = (a) => {
    let ans = ['', '', '', '', '', '', '', '', '']
    for (let i = 0; i < a.length; i++) {
      ans[a[i][1]] = a[i][0]
    }
    this.setState({ squareList: ans });
  }
  removeItem(index) {
    let a = this.state.history;
    a = a.slice(0, index);
    this.showRemoveItem(a);
  }
  render() {

    return (
      <div className="layout">
        <div>
          <h1>Tic Tac Toe</h1>
          <h3>User name : {this.state.username}</h3>
          <h4> nextPlayer:{this.state.nextPlayer}</h4>
          <h1>
            {this.state.playerWin}
          </h1>
          <Board squareList={this.state.squareList}
            setParentsState={this.setParentsState}
            nextPlayer={this.state.nextPlayer}
            isWin={this.state.isWin}
            playerWin={this.state.playerWin}
            history={this.state.history}
            count={this.state.count}
          />

        </div>
        <div>

          <ol>
            {this.state.history.map((item, index) => <div className="removeButton"><Button variant="light" onClick={() => this.removeItem(index)}>remove</Button>{item[0]} -{item[1]}</div>)}
          </ol>
        </div>

      </div>
    )
  }
}
