import React, { Component } from 'react'

export default class Square extends Component {
    constructor(props){
        super(props)
        this.state={
            value:"",
        }
    }
    selectSquare= ()=>{
       let array=this.props.squareList;
        array[this.props.id]="X"
        this.setState({squareList:array})
    }
    render() {
        return (
            <div className="square" onClick={()=>this.selectSquare()}>
                {this.state.value}
            </div>
        )
    }
}
