import React, { Component } from 'react'
import Square from './Square'
export default class Board extends Component {
    selectSquare=(id)=>{
        let array=this.props.squareList;
        array[id]="X"
        this.props.setParentsState({squareList:array})
    }




    render() {
        return (
            <div>
                <div style={{display:"flex"}}>
                    <Square id={0} selectSquare={this.selectSquare()}/>
                    <Square id={1} selectSquare={this.selectSquare()}/>
                    <Square id={2} selectSquare={this.selectSquare()}/>
                </div>
                <div style={{display:"flex"}}>
                    <Square id={3} selectSquare={this.selectSquare()}/>
                    <Square id={4} selectSquare={this.selectSquare()}/>
                    <Square id={5} selectSquare={this.selectSquare()}/>
                </div>
                <div style={{display:"flex"}}>
                    <Square id={6} selectSquare={this.selectSquare()}/>
                    <Square id={7} selectSquare={this.selectSquare()}/>
                    <Square id={8} selectSquare={this.selectSquare()}/>
                </div>

            </div>
        )
    }
}
